package com.dingapp.core.util;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStream;import java.io.InputStreamReader;import android.text.TextUtils;import com.dingapp.core.app.Application;public class TestJsonUtil {	public static String getTestJson(String fileName){			StringBuilder sb = new StringBuilder();			try {				InputStream is = Application.getInstance().getAssets()						.open(fileName);				BufferedReader br = new BufferedReader(new InputStreamReader(is));				String line = null;				while (!TextUtils.isEmpty(line = br.readLine())) {					sb.append(line);				}				br.close();			} catch (IOException e) {				e.printStackTrace();			}			return sb.toString();	}}